// Filter to last 7 days
const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

const recentFeedback = $input.all().filter(item => {
  const createdAt = new Date(item.json.created_at);
  return createdAt >= sevenDaysAgo;
});

// Group by type
const byType = {};
const byStatus = {};

recentFeedback.forEach(item => {
  const type = item.json.type || 'Other';
  const status = item.json.status || 'New';

  byType[type] = (byType[type] || 0) + 1;
  byStatus[status] = (byStatus[status] || 0) + 1;
});

return [{
  json: {
    total: recentFeedback.length,
    byType,
    byStatus,
    items: recentFeedback.map(f => f.json)
  }
}];

const feedback = $input.first().json;
const stats = $input.last().json;

const now = new Date();
const weekStart = new Date();
weekStart.setDate(weekStart.getDate() - 7);

const formatDate = (d) => d.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });

let html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px; }
    h1 { color: #2563eb; }
    h2 { color: #374151; margin-top: 30px; }
    .stat-box { background: #f3f4f6; padding: 15px; border-radius: 8px; margin: 10px 0; }
    .stat-number { font-size: 24px; font-weight: bold; color: #2563eb; }
    .stat-label { color: #6b7280; font-size: 14px; }
    table { width: 100%; border-collapse: collapse; margin: 15px 0; }
    th, td { padding: 10px; text-align: left; border-bottom: 1px solid #e5e7eb; }
    th { background: #f9fafb; }
    .badge { display: inline-block; padding: 4px 8px; border-radius: 4px; font-size: 12px; }
    .bug { background: #fef2f2; color: #dc2626; }
    .feature { background: #eff6ff; color: #2563eb; }
    .praise { background: #f0fdf4; color: #16a34a; }
    .footer { margin-top: 40px; padding-top: 20px; border-top: 1px solid #e5e7eb; color: #6b7280; font-size: 12px; }
  </style>
</head>
<body>
  <h1>üìä Weekly Feedback Report</h1>
  <p><strong>Period:</strong> ${formatDate(weekStart)} - ${formatDate(now)}</p>

  <h2>üìà Summary</h2>
  <div class="stat-box">
    <div class="stat-number">${feedback.total}</div>
    <div class="stat-label">Total Feedback Items</div>
  </div>

  <h2>üìù Feedback by Type</h2>
  <table>
    <tr><th>Type</th><th>Count</th></tr>
`;

Object.entries(feedback.byType || {}).forEach(([type, count]) => {
  html += `<tr><td><span class="badge ${type.toLowerCase()}">${type}</span></td><td>${count}</td></tr>`;
});

html += `
  </table>

  <h2>üìã Feedback by Status</h2>
  <table>
    <tr><th>Status</th><th>Count</th></tr>
`;

Object.entries(feedback.byStatus || {}).forEach(([status, count]) => {
  html += `<tr><td>${status}</td><td>${count}</td></tr>`;
});

html += `
  </table>

  <h2>üëç Rating Statistics</h2>
  <div class="stat-box">
    <div class="stat-number">${stats.helpful_percentage || 0}%</div>
    <div class="stat-label">Helpfulness Rating</div>
  </div>
  <table>
    <tr><td>Total Ratings</td><td>${stats.total || 0}</td></tr>
    <tr><td>üëç Helpful</td><td>${stats.helpful || 0}</td></tr>
    <tr><td>üëé Not Helpful</td><td>${stats.not_helpful || 0}</td></tr>
  </table>

  <div class="footer">
    <p>This report was automatically generated by SeggWat + n8n.</p>
    <p><a href="https://seggwat.com/dashboard">View Dashboard</a></p>
  </div>
</body>
</html>
`;

return [{ json: { html, subject: `Weekly Feedback Report - ${formatDate(weekStart)} to ${formatDate(now)}` } }];
